package com.key.heart.aprende;

enum Mexicana {

    AGUASCALIENTES("Aguascalientes","Aguascalientes. Se coloca el índice en posición de garra, y se estira y se encoge. Luego se coloca una c a la altura de la boca, y se mueve en círculo.", R.drawable.cat_mex_aguascalientes, R.drawable.img_aguascalientes),
    BAJA_CALIFORNIA("Baja California","Baja California. Se hace una letra B y se desliza hasta crear una letra C.", R.drawable.cat_mex_bc, R.drawable.img_bc),
    BAJA_CALIFORNIA_SUR("Baja California Sur","Baja California Sur. Se hace una letra B y se desliza hasta crear una letra C, después se hace una s, y se mueve en línea recta hacia abajo.", R.drawable.cat_mex_bcs, R.drawable.img_bcs),
    CAMPECHE("Campeche","Campeche. Se colocan el índice y el meñique sobre la ceja y la mejilla, tocando el rostro con las yemas, simulando un parche en el ojo.", R.drawable.cat_mex_campeche, R.drawable.img_campeche),
    CHIAPAS("Chiapas","Chiapas. Se hace la letra c con ambas manos, una frente a la otra. Luego se hace la h en ambas manos con las palmas hacia usted.", R.drawable.cat_mex_chiapas, R.drawable.img_chiapas),
    CHIHUACHUA("Chihuahua","Chihuahua. Se hace una letra c. Luego se coloca una h, sobre la barbilla.", R.drawable.cat_mex_chihuahua, R.drawable.img_chihuahua),
    CDMX("CDMX","CDMX. Se coloca una letra a sobre la palma de la otra mano, tocándola con la parte lateral del dedo meñique, y se mueve en círculo, después se crea la letra V, se lleva a la frente y se baja", R.drawable.cat_mex_cdmx, R.drawable.img_cdmx),
    COAHUILA("Coahuila","Coahuila. Se coloca una letra c sobre la frente, tocándola con el dorso del  pulgar, y se desliza hacia abajo tocándo suavemente la boca.", R.drawable.cat_mex_coahuila, R.drawable.img_coahuila),
    COLIMA("Colima","Colima. Se coloca la mano abierta con la palma hacia la oreja, a un lado de la frente, y se mueve en línea recta a un lado, hasta que queda la mano cerrada en punta.", R.drawable.cat_mex_colima, R.drawable.img_colima),
    DURANGO("Durango","Durango. Se hace una letra d a la altura de la oreja se rota la mano varias veces.", R.drawable.cat_mex_durango, R.drawable.img_durango),
    ESTADO_MEXICO("Estado de México","Estado de México. Se hace una garra con la mano y se golpea en la parte superior de la otra mano, luego se forma la letra v y se baja.", R.drawable.cat_mex_edomex, R.drawable.img_edomex),
    GUANAJUATO("Guanajuato","Guanajuato. Hacer la letra g ponerla de frente y moverla hacia adelante y hacia atrás.", R.drawable.cat_mex_guanajuato, R.drawable.img_guanajuato),
    GUERRERO("Guerrero","Guerrero. Con las yemas del índice y el meñique frente a frente, se dibuja una patilla sobre la mejilla.", R.drawable.cat_mex_guerrero, R.drawable.img_guerrero),
    HIDALGO("Hidalgo","Hidalgo. Se colocan los dedos pulgar, índice y medio en posición de garra, sobre un lado de la frente, tocándola con la punta del pulgar, y se mueven en ondas hacia abajo.", R.drawable.cat_mex_hidalgo, R.drawable.img_hidalgo),
    JALISCO("Jalisco","Jalisco. Se coloca la mano cerrada sobre la boca, tocándola con el dorso, y se mueve al frente, estirando el índice y el pulgar.", R.drawable.cat_mex_jalisco, R.drawable.img_jalisco),
    MICHOACAN("Michoacán","Michoacán. Se hace una letra m, señalando con la punta de los dedos al frente, moviendo la mano varias veces.", R.drawable.cat_mex_mich, R.drawable.img_michoacan),
    MORELOS("Morelos","Morelos. Se coloca la mano abierta a la altura de la cabeza, y se mueve hacia atrás, cerrando la mano al mismo tiempo.", R.drawable.cat_mex_morelos, R.drawable.img_morelos),
    NAYARIT("Nayarit","Nayarit. Se coloca una letra n a un lado de la frente, y se mueve en ondas sobre ella, recorriéndola.", R.drawable.cat_mex_nayarit, R.drawable.img_nayarit),
    NUEVO_LEON("Nuevo León","Nuevo León. Con una mano se rodea la otra en posición de capullo, que se desliza hacia arriba, rozando por el dorso la palma de la otra mano, y termina abierta. Luego se coloca la mano en posición de garra a un lado de la cabeza, y se desliza hacia abajo.", R.drawable.cat_mex_nuevoleon, R.drawable.img_nuevoleon),
    OAXACA("Oaxaca","Oaxaca. Se hace una letra o, y se rota la mano varias veces.", R.drawable.cat_mex_oaxaca, R.drawable.img_oaxaca),
    PUEBLA("Puebla","Puebla. Se coloca una letra p a la altura de la oreja, y se rota la mano varias veces.", R.drawable.cat_mex_puebla, R.drawable.img_puebla),
    QUERETARO("Querétaro","Querétaro. Se hace una letra q con la punta de los dedos señalando la oreja, y se rota la mano varias veces.", R.drawable.cat_mex_queretaro, R.drawable.img_queretaro),
    QUINTANA_ROO("Quintana Roo","Quintana Roo. Se hace una letra q, y se mueve en línea recta a un lado, se transforma en r, y termina con la letra o.", R.drawable.cat_mex_quintanaroo, R.drawable.img_quintanaroo),
    SAN_LUIS_POTOSI("San Luis Potosí","San Luis Potosí. Se hace una letra s. Luego se coloca una p sobre la frente, tocándola con la punta del dedo medio.", R.drawable.cat_mex_snpotosi, R.drawable.img_sanluispotosi),
    SINALOA("Sinaloa","Sinaloa. Se coloca una letra s a un lado de la frente, y se mueve en ondas hacia abajo.", R.drawable.cat_mex_sinaloa, R.drawable.img_sinaloa),
    SONORA("Sonora","Sonora. Se coloca la mano abierta con los dedos unidos sobre los labios, golpeándolos ligeramente, y se articula la letra o con los labios.", R.drawable.cat_mex_sonora, R.drawable.img_sonora),
    TABASCO("Tabasco","Tabasco. Se hace una letra t con el pulgar señalando la oreja, y se rota la muñeca hacia los lados.", R.drawable.cat_mex_tabasco, R.drawable.img_tabasco),
    TAMAULIPAS("Tamaulipas","Tamaulipas. Se hace una letra t, y con ella se golpea la palma de la otra mano.", R.drawable.cat_mex_tamaulipas, R.drawable.img_tamaulipas),
    TLAXCALA("Tlaxcala","Tlaxcala. Se hace la letra t en ambas manos, una frente a la otra, y se mueven al frente, hasta transformarse en l al mismo tiempo.", R.drawable.cat_mex_tlaxcala, R.drawable.img_tlaxcala),
    VERACRUZ("Veracruz","Veracruz. Se hace una letra v a la altura de la oreja, señalándola con la punta de los dedos, y se rota la mano varias veces.", R.drawable.cat_mex_veracruz, R.drawable.img_veracruz),
    YUCATAN("Yucatán","Yucatán. Se hace una letra y a la altura de la oreja, y se rota la mano varias veces.", R.drawable.cat_mex_yucatan, R.drawable.img_yucatan),
    ZACATECAS("Zacatecas","Zacatecas. Se dibuja una z desde la oreja hasta la altura del hombro.", R.drawable.cat_mex_zacatecas, R.drawable.img_zacatecas),;

    private final String nombre;
    private final String descripcion;
    private final int rutaSenia;
    private final int rutaImagen;

    Mexicana(String nombre,String descripcion, int rutaSenia, int rutaImagen){
        this.nombre = nombre;
        this.descripcion = descripcion;
        this.rutaSenia = rutaSenia;
        this.rutaImagen = rutaImagen;
    }

    public String getNombre() {
        return nombre;
    }

    public String getDescripcion() {
        return descripcion;
    }

    public int getRutaSenia() {
        return rutaSenia;
    }

    public int getRutaImagen() {
        return rutaImagen;
    }
}

